drop table skills;

set scan off;

create table skills(
  SEQUENCE NUMBER(5) NOT NULL,
  CATEGORY VARCHAR2(256) NOT NULL,
  SUBCATEGORY VARCHAR2(256) NOT NULL,
  SKILL VARCHAR2(256) NOT NULL,
  SKILL_ID VARCHAR2(10) NOT NULL,
  DESCRIPTION CLOB,
  CONSTRAINT TABLE_SKILLS_PK PRIMARY KEY ( SEQUENCE, CATEGORY, SUBCATEGORY, SKILL_ID )
);

